const result = [];

// Iterate through sorted data
for (const item of items) {
    const setId = item.json["data.data.phase.sets.nodes[0].id"];

    if (!seenSetIds.has(setId)) {
        seenSetIds.add(setId);
        result.push(item);
    }
}

// Return filtered results
return result;

// Define max tournament name length
const maxLength = 40;

// Define key phrases to be hashtagged, replacing spaces with no-space formatting
const hashtagKeywords = {
    "Rivals2": "#Rivals2",
    "Rivals of Aether": "#RivalsOfAether",
    "Melee": "#Melee",
    "Rivals": "#Rivals",
    "Smash": "#Smash",
    "Ultimate": "#Ultimate",
    "Aether": "#Aether"
};

// Function to replace keywords with their hashtag versions
const addHashtags = (name) => {
    let updatedName = name;
    Object.keys(hashtagKeywords).forEach((word) => {
        const regex = new RegExp(`\\b${word}\\b`, "gi");
        updatedName = updatedName.replace(regex, hashtagKeywords[word]);
    });
    return updatedName;
};

// Function to truncate names while keeping key details
const truncateName = (name) => {
    let truncatedName = addHashtags(name);

    if (truncatedName.length <= maxLength) {
        return truncatedName;
    }

    const splitCharacters = ["-", "@", "#", ":", "|"];
    let segments = [truncatedName];

    splitCharacters.forEach((char) => {
        if (truncatedName.includes(char)) {
            segments = truncatedName.split(char).map(segment => segment.trim());
        }
    });

    let cleanedName = segments[0];
    for (let i = 1; i < segments.length; i++) {
        const segment = segments[i];
        if (Object.keys(hashtagKeywords).some(key => segment.includes(key))) {
            cleanedName += `, ${segment}`;
        }
    }

    if (cleanedName.length > maxLength) {
        cleanedName = cleanedName.substring(0, maxLength).trim();
        if (cleanedName.includes(" ")) {
            cleanedName = cleanedName.substring(0, cleanedName.lastIndexOf(" "));
        }
        cleanedName += "...";
    }

    return cleanedName;
};

// Define mapping of character names to icons
const characterIcons = {
    "Forsburn": "â˜ ï¸",
    "Loxodont": "ðŸ˜",
    "Clairen": "ðŸ¤º",
    "Zetterburn": "ðŸ¦",
    "Wrastor": "ðŸ¦…",
    "Fleet": "ðŸ¦Š",
    "Maypul": "ðŸŒ±",
    "Kragg": "ðŸª²",
    "Ranno": "ðŸ¸",
    "Orcane": "ðŸ‹",
    "Etalus": "ðŸ»â€",
    "Olympia": "ðŸ’Ž"
};

// Retrieve total entrants
const totalEntrants = $input.first().json.data[0].$json['sum_\'data_data_tournaments_nodes\'_events0_numEntrants'] || 0;

// Retrieve and process tournament data
const tournamentData = Array.from({ length: 5 }, (_, i) => {
    const tournamentNode = $input.first()?.json?.data?.[i]?.["data.data.tournaments.nodes"] || {};
    const eventNode = $input.first()?.json?.data?.[i]?.["data.data.set.event"] || {};
    const winnerName = $input.first()?.json?.data?.[i]?.winnerName || "";

    // Get the tournament name
    const baseName = eventNode.tournament?.name || tournamentNode.name || "N/A";

    // Get location from tournament data
    const location = tournamentNode.countryCode && tournamentNode.countryCode !== "US" 
        ? tournamentNode.countryCode 
        : tournamentNode.addrState || "";

    // Append location if available
    const nameWithLocation = location ? `${baseName} (${location})` : baseName;

    // Find correct entrant count using the provided reference
    const entrants = $input.first()?.json?.data?.[i]?.["['data.data.tournaments.nodes'].events"]?.numEntrants || 0;

    // Retrieve character selection from first game (if available)
    const selections = $input.first()?.json?.data?.[i]?.["data.data.set.games[0].selections"] || [];
    let characterName = "";

    if (Array.isArray(selections)) {
        characterName = selections.find(selection => selection?.character?.name)?.character?.name || "";
    } else if (selections.character) {
        characterName = selections.character.name;
    }

    // Get icon for character or default "W:"
    const icon = characterIcons[characterName] || "W:";

    return {
        originalName: nameWithLocation,
        truncatedName: truncateName(nameWithLocation),
        entrants,
        winner: winnerName ? `   ${icon} ${winnerName}` : ""
    };
});

// Ensure there are at least 5 tournament entries
while (tournamentData.length < 5) {
    tournamentData.push({ originalName: "N/A", truncatedName: "N/A", entrants: 0, winner: "" });
}

// Construct tournament list
let tournamentList = "\n\n";
tournamentData.forEach((tournament) => {
    tournamentList += `${tournament.truncatedName} - ${tournament.entrants} entrants\n${tournament.winner}\n`;
});

// Construct final message
const inputString = "Yesterday in #Rivals2!";
const finalString = `${inputString}${tournamentList}\nEntrants yesterday: ${totalEntrants}`.trim();

// Return result
return [{ 
    result: finalString, 
    characterCount: finalString.length 
}];

parameters.query:
=query SetInfo($setId: ID!) {
    set(id: $setId) {
        id
        winnerId
        games {
            selections {
                character {
                    name
                }
                entrant {
                    name
                    id
                }
            }
        }
        displayScore
        event {
            tournament {
                name
            }
            id
        }
    }
}

