    collection: "app.bsky.feed.post",
    record: {
        "$type": "app.bsky.feed.post",
        text: replyText,
        reply: {
            root: {
                cid: $('If').first().json.cid,
                uri: $('If').first().json.uri
            },
            parent: {
                cid: $('If').first().json.cid,
                uri: $('If').first().json.uri
            }
        },
        createdAt: futureDate.toISOString()
    }
};

return replyPostData;
