parameters.jsCode:
// Datos de entrada
const listFollowers = $('List followers').all()[0].json.followers;
const extractFromFile = $('Extract from File').all()[0].json.data[0].followers;

// Verificar que tenemos acceso a los datos
console.log('listFollowers length:', Array.isArray(listFollowers) ? listFollowers.length : 'no es array');
console.log('extractFromFile length:', Array.isArray(extractFromFile) ? extractFromFile.length : 'no es array');

// Mostrar algunos ejemplos de cada lista
console.log('Ejemplo de listFollowers:', listFollowers?.slice(0, 2));
console.log('Ejemplo de extractFromFile:', extractFromFile?.slice(0, 2));

// Crear conjunto de DIDs del archivo extraÃ­do
const existingDids = new Set(extractFromFile?.map(item => item.did) || []);
console.log('DIDs existentes:', Array.from(existingDids).slice(0, 5));

// Filtrar listFollowers
const newFollowers = listFollowers?.filter(follower => !existingDids.has(follower.did)) || [];

return {
  json: {
    debug: {
      listFollowersCount: listFollowers?.length || 0,
      extractFromFileCount: extractFromFile?.length || 0,
      existingDidsCount: existingDids.size,
      newFollowersCount: newFollowers.length
    },
    newFollowers,
    newDids: newFollowers.map(follower => follower.did),
    count: newFollowers.length
  }
}

